import React,{Component} from 'react';
import style from '../Component/css/Read.module.scss'
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import { parseISO, format } from 'date-fns'
import Avatar from '@material-ui/core/Avatar';
import {POST_READ} from "../Component/graphalQurey"
import { useRouter } from 'next/router'
import {client} from "../Component/apollo-client";
import ReadCard from "../Component/Read/ReadCard";


import Link from 'next/link';
import gql from 'graphql-tag';
import Head from 'next/head'
import SvgIcon from '@material-ui/core/SvgIcon';
import TriggerMenu from "../Component/Artical/Menu";

export default function Read(props) {
  const { getData,statusCode }= props;   
  const {data} = getData
  const {post} = data
  const postID= post.id
  const date = parseISO(post.date)
  console.log(post.categories.nodes, ' post.categories.nodes ')
  if (statusCode) {
    return <Error statusCode={statusCode} />
  }else{
    return (
      <>  
           <Head> 
              <link rel='stylesheet' id='wp-block-library-css'  href='https://www.rajdeepsingh.dev/wp-includes/css/dist/block-library/style.min.css?ver=5.4.2' media='all' />
              <link rel='stylesheet' id='wp-block-library-theme-css'  href='https://www.rajdeepsingh.dev/wp-includes/css/dist/block-library/theme.min.css?ver=5.4.2' media='all' />
              {/* <link rel='stylesheet' id='genericons-css'  href='https://www.rajdeepsingh.dev/wp-content/themes/writing/genericons/genericons.css?ver=3.2' type='text/css' media='all' /> */}
              {/* <link rel='stylesheet' id='fontawesome-css'  href='https://www.rajdeepsingh.dev/wp-content/themes/writing/framework/font-awesome/css/font-awesome.min.css?ver=1' type='text/css' media='all' /> */}
              {/* <link rel='stylesheet' id='asalah-plugins-css'  href='https://www.rajdeepsingh.dev/wp-content/themes/writing/pluginstyle.css?ver=1' type='text/css' media='all' />
              <link rel='stylesheet' id='asalah-style-css'  href='https://www.rajdeepsingh.dev/wp-content/themes/writing/style.css?ver=3.530' type='text/css' media='all' /> */}
            </Head> 
  
          <div className={style.outerCard}>
                  <Typography component="h5" variant="subtitle1">
                      #{post.categories.edges[0].node.name}
                  </Typography>
                  <Typography component="h1" variant="h1" dangerouslySetInnerHTML={{ __html: post.title }}>
                  </Typography>           
                  <div className={style.cardBox}>
                      <Avatar alt={post.author.name} src={post.author.avatar.url}  className={style.imageSize} />
                      <Typography component="h5" title={post.author.name} variant="h5" className={style.cardBoxAuthorTitle}>
                        {post.author.name} 
                      </Typography>
                  </div>
                  
                  <CardMedia
                      component="img"
                      alt={post.featuredImage.altText}
                      image={post.featuredImage.sourceUrl}
                      title={post.featuredImage.title}
                      className={style.cardArticalImage}
                  />
                  <time className=" MuiTypography-root MuiTypography-h5" dateTime={"2020-02-18T07:54:18"} >{format(date, 'LLLL	d, yyyy')}</time>
                
                  <div key={  post.id} className={style.innerHtmlFor} dangerouslySetInnerHTML={{ __html: post.content }}></div>                  
              </div>

          <div className={style.writeComment}>
          {
            (post.commentStatus)?
                <Link href={`/comments/?id=${post.id}`} as={ `/Comment/${post.slug}-${post.id}`}> 
                <a> { post.commentCount? post.commentCount : 0} Comment Now </a> 
              </Link> :
            <Link> 
                <a> Comment Desible </a> 
            </Link>
          }   
          </div>

          <Grid className={style.grid} container  >

          { (post.categories.nodes)?<Grid item sm={12} md={12} lg={12} xs={12}>   
                <Typography className={style.MoreStory} component="h4" variant="h3">
                    More Story 
                </Typography>
          </Grid>:"" }
          
          
            {      
              post.categories.nodes[0].posts.nodes.map(function(post, i) {                     
                    return (postID === post.id)? '' : (
                  <Grid key={post.id} item sm={6} md={6} lg={6} xs={12}>
                    <Card className={style.cardMore}>
                        <CardActionArea>
                                <CardMedia
                                    className={style.imageCardMore}
                                    component="img"
                                    alt={post.featuredImage.altText}
                                    image={post.featuredImage.sourceUrl}
                                    title={post.featuredImage.altText}
                                />
                                <CardContent>
                                    <Link href={`/[read]?id=${post.id}`} as={ `${post.slug}-${post.id}`}>
                                        <a className='MuiTypography-root MuiTypography-h5 MuiTypography-gutterBottom' dangerouslySetInnerHTML={{ __html: post.title }}>
                                        </a> 
                                    </Link>                
                                </CardContent>
                                <CardActions  style={{'justifyContent': 'space-between' ,padding:'2px'}}>
                                        <div className={style.views}>
                                            <SvgIcon width="48" height="48" viewBox="0 0 48 48" className={style.iconEyes}>
                                            <path fillRule="evenodd" clipRule="evenodd" d="M39.6594 23.05C34.5637 17.1 29.1652 14 24.0189 14C18.8727 14 13.4742 17.1 8.3784 23.05C7.87387 23.6 7.87387 24.45 8.3784 25C13.4742 30.9 18.8727 34 24.0189 34C29.1652 34 34.5637 30.9 39.6594 24.95C40.1135 24.4 40.1135 23.6 39.6594 23.05ZM24.0189 32C19.6295 32 14.7355 29.15 10.1947 24C14.7355 18.85 19.6295 16 24.0189 16C28.4084 16 33.3023 18.85 37.8431 24C33.3023 29.15 28.4084 32 24.0189 32ZM24.0189 18C20.689 18 17.9645 20.7 17.9645 24C17.9645 27.3 20.689 30 24.0189 30C27.3488 30 30.0733 27.3 30.0733 24C30.0733 20.7 27.3488 18 24.0189 18ZM24.0189 28C21.799 28 19.9827 26.2 19.9827 24C19.9827 21.8 21.799 20 24.0189 20C26.2389 20 28.0552 21.8 28.0552 24C28.0552 26.2 26.2389 28 24.0189 28Z" fill="black"/>
                                            </SvgIcon>
                                            <h3 title="Views">2022</h3>
                                        </div>
                                        <TriggerMenu slug={ `${post.slug}-${post.id}`} postId={post.id}  />
                                </CardActions>
                        </CardActionArea>
                </Card>
          </Grid>)

                    }    
              ) 
            }               
          </Grid> 
      </>
    )
  }
}


export const  getServerSideProps =  async ({ pathname, query, asPath, req, res, err }) =>{               
        
        if(query.slug){
          const id=query.slug
              try {
                const getData= await client.query(({
                    query:POST_READ,
                    variables:{id}
                }))
                return {props:{ getData}}
          }
          catch(error){
            return { props: { statusCode: 404 } }
          }
        }
        else {
            const id= query.Read.split( '-' ).pop()
            try {
              const getData= await client.query(({
                  query:POST_READ,
                  variables:{id}
              }))
              return {props:{ getData}}

            }
            catch(error){
              return { props: { statusCode: 404 } }
            }
          }    
}
