import '../Component/css/style.scss'
import '../Component/css/reactMde.scss'


import {client} from "../Component/apollo-client";
import App from 'next/app'


import POST_HEADER,{POST_QUERY, POST_READ} from "../Component/graphalQurey"
import { ApolloProvider } from "@apollo/react-hooks";

export default function MyApp({ Component, pageProps }) {
 return (
   <ApolloProvider client={client}> 
     <Layout {...data} >
      <Component {...pageProps} />
 </Layout>
   </ApolloProvider>

  )
}

Read.getInitialProps =  async ()=> {  

   
      const getMenu= await client.query(({
          query:POST_HEADER    
      })) 
      const getPost= await client.query(({
        query:POST_QUERY    
      })) 
            return  { data : { getMenu ,getPost }}
    
     
}
