import React, { Component } from 'react'
import gql from 'graphql-tag';
import Head from 'next/head'
import {client} from "../../Component/apollo-client";
import Typography from '@material-ui/core/Typography';
import style from "../../Component/css/Page.module.scss";
import {PAGE_QUERY} from "../Component/graphalQurey"


export default class page extends Component {

    static async getInitialProps({query}) {
        
        const id =   query.Page.split( '-' ).pop() ;

        console.log(query, ' ctx query FORM QUERY ')
                
        const PAGE_QUERY = gql`query GET_PAGE($id: ID!) {
           
            page(id: $id) {
                content
                title
                featuredImage {
                  altText
                  sourceUrl(size: LARGE)
                  id
                }
              }
            
           }`
        
       try {
          const res= await client.query(({
              query:POST_QUERY,
              variables:{id}
          }))
          return { data: res}
        }
        catch(error){
          console.log(error)
        }
    }
    render() {
        
        const {data} = this.props;
        return (
            <>
            <Head> 
              <link rel='stylesheet' id='wp-block-library-css'  href='https://www.rajdeepsingh.dev/wp-includes/css/dist/block-library/style.min.css?ver=5.4.2' media='all' />
              <link rel='stylesheet' id='wp-block-library-theme-css'  href='https://www.rajdeepsingh.dev/wp-includes/css/dist/block-library/theme.min.css?ver=5.4.2' media='all' />
            {/* <link rel='icon' href={post.featuredImage.sourceUrl} type='image/x-icon'/> */}
            </Head>

                  <Typography component="h1" variant="h1" className={style.pageHeading} dangerouslySetInnerHTML={{ __html: data.data.page.title }}>
                      </Typography>           
                   <Typography component="div" className={style.pageStyle}  dangerouslySetInnerHTML={{ __html: data.data.page.content }}>
                    </Typography> 
            </>
        )
    }
}
