import gql from 'graphql-tag';

export  const POST_QUERY = gql`query postData {
  __typename
  posts {
  edges{
    node {
      id
      title
      slug
      excerpt
      categories {
        nodes {
          name
        }
      }
      featuredImage {
        
          altText
          title
          sourceUrl
        
      }
    }
    }
  }
}` 

export const POST_READ = gql`query GET_POST($id: ID!) {
    post(id: $id) {
       id
       commentCount
       title
       content
       slug
       date
       commentStatus
       featuredImage {
         altText
         sourceUrl
         title
       }
       categories {
         edges {
           node {
             name
           }
         }
       }
       author {
         name
         avatar {
           url
         }
       }
       categories {
        nodes {
          posts {
            nodes {
              title
              id
              slug
              featuredImage{
                sourceUrl
                altText
              }
            }
          }
        }
      }
     }
   }`

export const POST_HEADER= gql`query Menu {
      __typename
     menu(id:"TWVudToyMg==") {
        id
        name
        slug
         menuItems {
            nodes {
              label
              menuItemId
              id
              url
              connectedObject {
                __typename
                ... on Page {
                  id
                  title
                  slug
                }
                ... on Post {
                  id
                  title
                }
              }
            }
          }
      }
    }`

const POST_FOOTER= gql`query Menu {
      __typename
     menu(id:"TWVudToyMg==") {
        id
        name
        slug
         menuItems {
            nodes {
              label
              menuItemId
              id
              url
              connectedObject {
                __typename
                ... on Page {
                  id
                  title
                  slug
                }
                ... on Post {
                  id
                  title
                }
              }
            }
          }
      }
    }`

export default POST_FOOTER
