import React from 'react'
import ReactMde from "react-mde";
import * as Showdown from "showdown";
import gql from 'graphql-tag';
import { useMutation } from '@apollo/react-hooks';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';

import Button from '@material-ui/core/Button';
import style from '../css/Comments.module.scss'


const signUP = (userData) => {
  return  request('https://www.rajdeepsingh.dev/graphql', COMMENT_MUTATION, userData);
};



function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

 export default  function CommentEditor(props) {
 

    const [value, setValue] = React.useState();
    const [selectedTab, setSelectedTab] = React.useState("write");
    const [input, { data } ] = useMutation(CommentAdd);
    const [open, setOpen] = React.useState(true);

    const converter = new Showdown.Converter({
      tables: true,
      simplifiedAutoLink: true,
      strikethrough: true,
      tasklists: true
    });
  
  
    
    

    const SuccessFull= (data)? data.createComment.success :""
    const submitComment= e => {
    //  const userId= localStorage.getItem("userId");
      // localStorage.setItem("userId", data.login.user.userId);

        e.preventDefault();


        input( 
            {
                variables:{      
                    input : {
                        clientMutationId: "createComment",
                        content: value,
                        userId: props.userid, 
                        author: props.userName, 
                        commentOn: props.postid
                    }
                    
                }
            }
            )      
            
        }
      
     const handleClose = () => {      
         setOpen(false);
      };
       
     
    return (
      <div key={props.keyId} className="container">
        
        <form onSubmit={submitComment} > 
  
          <ReactMde
            value={value}
            onChange={setValue}
            selectedTab={selectedTab}
            onTabChange={setSelectedTab}
            generateMarkdownPreview={markdown =>
              Promise.resolve(converter.makeHtml(markdown))
            }
            childProps={{
              writeButton: {
                tabIndex: -1
              }
            }}
  
          />
  
            <Button type="submit" className={style.productButton} size="small">
                             Submit  
            </Button>
        </form>
      
        {
            (SuccessFull) ? <Snackbar open={open} anchorOrigin={ 
                {    vertical: 'top', 
                    horizontal: 'center' }
                } 
                autoHideDuration={6000} onClose={handleClose}>
                  <Alert onClose={handleClose} severity="success">
                        Your Comment success Full Submit 
                  </Alert>
              </Snackbar> : ''
        
        }

       
      </div>
    );
  }
