import React, { Component } from 'react'
import Head from 'next/head'
import {client} from "../../Component/apollo-client";
import Typography from '@material-ui/core/Typography';
import style from "../../Component/css/Page.module.scss";
import {PAGE_QUERY} from "../../Component/graphalQurey"


export default class page extends Component {

    static async getInitialProps({query}) {
        
        const id = query.Page.split( '-' ).pop() ;         
        
       try {
          const getData= await client.query(({
              query:PAGE_QUERY,
              variables:{id}
          }))
           return {getData}
        }
        catch(error){
            const concerter = JSON.stringify(error)
            return {concerter} 
        }
    }

    render() {
        
        const {data,concerter} = this.props;

        console.log( this.props , ' check out on page')
        // console.log( concerter , ' concerter out on page')

 


     if (data===undefined) {
        // return <Error />
      } 

    else {
        return (

            
                <div className={style.page}> 
                  <Typography component="h1" variant="h1" className={style.pageHeading} dangerouslySetInnerHTML={{ __html: data.data.page.title }}>
                      </Typography>           
                   <div  className={style.pageStyle}  dangerouslySetInnerHTML={{ __html: data.data.page.content }}>
                    </div> 
                </div>

        )
    }
    }
}
