import React from 'react'
import ReactMde from "react-mde";
import * as Showdown from "showdown";
import gql from 'graphql-tag';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';

import Button from '@material-ui/core/Button';
import style from '../css/Comments.module.scss'
import { request } from 'graphql-request';
import {COMMENT_MUTATION} from "../graphalQurey"
import useSWR from 'swr'


const signUP = (input) => {
  return  request('https://www.rajdeepsingh.dev/graphql', COMMENT_MUTATION, input);
};

// const fetcher = query => request('https://api.graph.cool/simple/v1/movies', query)


function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
  }

 export default  function CommentEditor(props) {
  const [value, setValue] = React.useState();
    const [fetchdata, setFetchdata  ] = React.useState();
    const [selectedTab, setSelectedTab] = React.useState("write");
    // const [userId, setUserId] = React.useState("");
    // const [displayName, setDisplayName] = React.useState("");

    //  const { data } = useSWR(isReady ? '/api/user' : null, fetcher, { suspense: true })

  // const { data, error } =  useSWR( fetchdata ? COMMENT_MUTATION : null ,)


    const [open, setOpen] = React.useState(true);
    const [comment, setComment] = React.useState({});

    const converter = new Showdown.Converter({
      tables: true,
      simplifiedAutoLink: true,
      strikethrough: true,
      tasklists: true
    });



    const submitComment= async (e) => {
        e.preventDefault();          

          const getUserId= localStorage.getItem("userId")
          const getDisplayName= localStorage.getItem("displayName");

         const getData= await signUP( {
            "input":{
                    "content": value,
                    "author": getDisplayName, 
                    "userId":getUserId,
                    "clientMutationId": "createComment",
                    "commentOn": props.postid
            }
          }  
        ).then(
          (data)=>{
            setComment( ()=> {data} )
          }
        ).catch(
          setComment((error)=> {error})
        )
        console.log(getData,'data Comment ')

          // console.log(getData, ' inside getData');

        }

     const handleClose = () => {      
         setOpen(false);
      };
      console.log(fetchdata, ' inside useEFFEct');

    return (
      <div key={props.keyId} className="container">

        <form onSubmit={submitComment} > 

          <ReactMde
            value={value}
            onChange={setValue}
            selectedTab={selectedTab}
            onTabChange={setSelectedTab}
            generateMarkdownPreview={markdown =>
              Promise.resolve(converter.makeHtml(markdown))
            }
            childProps={{
              writeButton: {
                tabIndex: -1
              }
            }}

          />

            <Button type="submit" className={style.productButton} size="small">
                             Submit  
            </Button>
        </form>

        {
            (fetchdata) ? <Snackbar open={open} anchorOrigin={ 
                {    vertical: 'top', 
                    horizontal: 'center' }
                } 
                autoHideDuration={6000} onClose={handleClose}>
                  <Alert onClose={handleClose} severity="success">
                        {fetchdata} 
                  </Alert>
              </Snackbar> : ''

        }


      </div>
    );
  }
