import React, { Component , StrictMode } from 'react';
import Layout from '../Component/Layout'
import Grid from '@material-ui/core/Grid';
import MainSection from '../Component/MainSection/MainSection'
import Artical from '../Component/Artical/Artical'
import {POST_QUERY} from "../Component/graphalQurey"
import Myapp from './_app.js';
import Head from 'next/head';
import {client} from "../Component/apollo-client";
import ErrorBoundaries from "../Component/ErrorBoundaries";

import Error from 'next/error'


export default function Index({data,errorCode}) {
    //  const {data} = getData
    const {posts} = data

    if (errorCode) {
      return <Error statusCode={errorCode} />

    }
console.log(data , ' data ')
  return (
              <Grid component='article' container >
                  {
                    posts.edges.map(
                      (post, i) =>{
                            if(i === 0 ){
                              return <MainSection key={post.node.id} post={post} />
                            } else {
                              return <Artical key={post.node.id}  post={post} />   
                            }
                        }
                    )
                  }
              </Grid>

  )
}

export const  getServerSideProps =  async () =>{
  try{
       const getData= await client.query(({
          query:POST_QUERY    
        }))   
        const errorCode = getData.ok ? false : getData.statusCode
        // const json = await getData.json()
        data = JSON.parse(JSON.stringify(getData))
        error = JSON.parse(JSON.stringify(errorCode))

        // console.log(getData, ' v ')

        return {
          props: { error, data},
        }
    }
    catch(error) {
    console.error(error )
  }

}
