import React, { Component } from 'react'
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Avatar from '@material-ui/core/Avatar';
import {POST_COMMENT} from "../../Component/graphalQurey"

import CommentsCard from '../../Component/CommentsCard/CommentsCard'

import style from '../../Component/css/Comments.module.scss'

import Head from 'next/head'
import gql from 'graphql-tag';

import {client} from "../../Component/apollo-client";
import Link from 'next/link';
import CommentEditor from '../../Component/CommentEditor/CommentEditor'



export default function Comment() {
  const {data}= this.props

  return (
    <>       
      <Head> 
        <meta charset="utf-8" />
    </Head>
      <Container className={style.root}>
      
        <div className={style.cardInner}>
            <Typography component="h3" variant="h3"  className={style.heading}>
                Write a Comment
            </Typography>
            <Card className={style.cardBox}>
            <Avatar alt={data.data.post.featuredImage.altText} src={data.data.post.featuredImage.sourceUrl} className={style.imageSize} />
            <Link href={`/[read]?id=${data.data.post.id}`} as={ `${data.data.post.slug}-${data.data.post.id}`}>
                <a dangerouslySetInnerHTML={{ __html: data.data.post.title }} target="_blank" className={style.cardBoxTitle}>
                </a>
            </Link>
            </Card>
        </div>
      
        <div className={style.editorStyle}> 


          <CommentEditor keyId={data.data.post.id} userid={data.data.post.author.userId } userName={data.data.post.author.name } postid={data.data.post.postId}/>
        

         
        </div> 
     
      { 
        data.data.post.comments && data.data.post.comments.nodes.map(
          (ReadComments)=>{
              return (
                <div key={ReadComments.id}>
                  <CommentsCard  data={ReadComments} />
                  { 
                    ReadComments.replies && ReadComments.replies.nodes.map(
                        replies=>{
                            return <CommentsCard   reply={true} data={replies} />
                        }
                      )
                  } 

                </div>
              )       
          }
       )
      }
  </Container>  
    </>
  )
}


