export const  getServerSideProps =  async ({  query })=> {
          
  const id = query.Comment.split( '-' ).pop() ;       
  
  try{
    const getData= await client.query(({
      query:POST_COMMENT,
      variables:{id}
    }))
    return{ props: { ...getData }}
  }
  catch(error){
    return { props: { statusCode: 404 } }
  }

}

